import React from 'react';
import axios from "axios";
const API_GET_TODOS = "http://localhost:3502/api/todos";

const Paginate = ({ todosPerPage, numbersOfTodos, setCurrentPage, getTodos, filter, currentPage }) => {
    console.log(currentPage);
    const pageNumbers = [];

    const pag = (number) => {
        setCurrentPage(number);
        pagination();
    }

    const pagination = async (req) => {
        try {
            for (let i = 1; i <= (Math.ceil(numbersOfTodos / todosPerPage)); i++) {
                pageNumbers.push(i);
                const result = await axios.get(API_GET_TODOS, {
                    page: req.body.currentPage
                });
            }
            getTodos();
        }
        catch (err) {
            console.log(err);
            alert(err);
        }
    }
    console.log(currentPage);
    return (
        <div>
            <div className="mainSelect">
                {numbersOfTodos > 0 ?
                    <div className="inSelect">
                        <button onClick={() => { pag(1) }} className="selectArrow">❮</button>
                        {pageNumbers.map((number => (
                            <div key={number} select>
                                <button onClick={() => pag(number)} href="!#" className="select">
                                    {number}
                                </button>
                            </div>
                        )))}
                        <button onClick={() => { pag(Math.ceil(numbersOfTodos / todosPerPage)) }} className="selectArrow">❯</button>
                    </div>
                    : ""}
            </div>
        </div>
    )
}

export default Paginate;
