import "../css/style.css";
import axios from "axios";
import { useState, useContext } from "react";
import { AuthContext } from "../context/auth.context";
const API_GET_TODOS = "http://localhost:3505/api/todos";


const TodoItem = ({ todo, removeItem, changeStatus, getTodos, }) => {
    const [currentTitle, setCurrentTitle] = useState(todo.name);
    const [showInput, setShowInput] = useState(false);
    const { token } = useContext(AuthContext);
    const editItem = async (e, currentTitle, todo) => {
        try {
            console.log(todo);
            if (e.keyCode === 13) {
                await axios(API_GET_TODOS + `/${todo}`, {
                    method: "PATCH",
                    data: {
                        name: currentTitle,
                        done: todo.done !== "done" ? "done" : "undone",
                    },
                    headers: {
                        Authorization: "Bearer " + token
                    }
                });
                e.target.blur();
                setShowInput(false);
            }
            if (e.keyCode === 27) {
                e.target.blur();
                console.log(todo);
                setCurrentTitle(e.target.value);
                setShowInput(false);
            }
            getTodos();
        } catch (err) {
            console.log(err);
            alert(err);
        }
    };
    return (
        <div key={todo.uuid}>
            <div className="todoElement">
                <div className="inElement">
                    <input
                        type="checkbox"
                        className="buttonComplete"
                        onClick={() => {
                            changeStatus(todo);
                        }}
                    />
                    {!showInput ? (
                        <span onDoubleClick={() => setShowInput(true)}>
                            {currentTitle}
                        </span>
                    ) : (
                        <input
                            value={currentTitle}
                            onKeyDown={(e) =>
                                editItem(e, currentTitle, todo.id)
                            }
                            className="sideInput"
                            onChange={(e) => setCurrentTitle(e.target.value)}
                        />
                    )}
                </div>
                <div className="inElement">
                    <div>
                        {new Date(Date.parse(todo.updatedAtt)).toLocaleString()}
                    </div>
                    <input
                        type="button"
                        value="ðŸ—‘"
                        className="buttonDelete"
                        onClick={() => {
                            removeItem(todo.id);
                        }}
                    />
                </div>
            </div>
        </div>
    );
};

export default TodoItem;
