import StateButtons from "./StateButtons";
import { v4 as uuidv4 } from 'uuid';
import { useState } from "react";

const TodoList = (props) => {
    const [filter, setFilter] = useState("undone");
    const date = () => {
        const date = new Date();
        return date.getDate() + '.' + date.getMonth() + '.' + date.getFullYear() + ' ' + date.getHours() + ':' + date.getMinutes() + ':' + date.getSeconds()
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        props.setTodos((prevValue) => [
            ...prevValue,
            { id: uuidv4(), title: props.text, date: date(), status: "undone" },
        ]);
        props.setText("");
    };


    const handleChange = (e) => {
        props.setText(e.target.value);
    };


    let taskElements = props.todos.map(e => <StateButtons title={e.title} key={e.id} removeItem={props.removeItem} setTodos={props.setTodos} todos={props.todos} id={e.id} date={e.date} setText={props.setText} text={props.text} />)
    console.log([props.todos])
    return (
        <form onSubmit={handleSubmit}>
            <div className="title">
                <h1>ToDo</h1>
            </div>
            <div className="mainInput">
                <input
                    type="text"
                    required
                    placeholder="I want to..."
                    value={props.text}
                    onChange={handleChange}
                    className="input"
                ></input>
            </div>
            <div className="mainInterface">
                <div>
                    <button className="buttons" onClick={(e) => setFilter(e.target.value)}
                        value={filter}>All</button>
                    <button className="buttons" onClick={(e) => setFilter(e.target.value)}
                        value={filter}>Done</button>
                    <button className="buttons" onClick={(e) => setFilter(e.target.value)}
                        value={filter}>Undone</button>
                </div>
                <div className="saideInterface">
                    <h4>Sort by date:</h4>
                    <button className="arrows">⤊</button>
                    <button className="arrows">⤋</button>
                </div>
            </div>
            <div className="todoList">
                {taskElements}
            </div>
            <div className="mainSelect">
                <button className="select">❮</button>
                <button className="select">1</button>
                <button className="select">2</button>
                <button className="select">3</button>
                <button className="select">4</button>
                <button className="select">5</button>
                <button className="select">❯</button>
            </div>

        </form>
    );
}

export default TodoList;
