import StateButtons from "../TodoList/StateButtons"
import { v4 as uuidv4 } from 'uuid';



const TodoList = (props) => {
    const editItem = (currentTitle, id, e) => {
        console.log(e);
        let oldName;
        if (e.keyCode === 13) {
            const item = props.filtered.find(el => el.id === id);
            oldName = item.title;
            item.title = currentTitle
            const itemIndex = props.filtered.findIndex(el => el.id === id);
            const arr = props.filtered
            arr[itemIndex] = item
            props.setTodos(arr);
        }
        if (e.keyCode === 27) {
            e.target.blur();
            e.target.value = oldName;

        }

    }
    let taskElements = props.filtered.map(e => <StateButtons removeItem={props.removeItem} setTodos={props.setTodos} todos={props.todos} setText={props.setText} text={props.text} changeStatus={props.changeStatus} todo={e} editItem={editItem} />)
    return (
        <div className="todoList">
            {taskElements.map((task) => {
                return (<div key={uuidv4()}>{task}</div>)
            })}
        </div>
    );
}

export default TodoList;
