import StateButtons from "./StateButtons";
import { v4 as uuidv4 } from 'uuid';
import { useState, useEffect } from "react";



const TodoList = (props) => {
    const [filtered, setFiltered] = useState(props.todos);
    console.log('------>', props.todos)

    useEffect((todos) => {
        setFiltered(todos)
    }, [props.todos])

    const date = () => {
        const date = new Date();
        return date.getDate() + '.' + date.getMonth() + '.' + date.getFullYear() + ' ' + date.getHours() + ':' + date.getMinutes() + ':' + date.getSeconds()
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        props.setTodos((prevValue) => [
            ...prevValue,
            { id: uuidv4(), title: props.text, date: date(), status: "undone" },
        ]);
        props.setText("");
    };

    const todoFilter = (status) => {
        if (status === "All") {
            setFiltered(props.todos);
        } else {
            console.log('status: ', status);
            let filteredTodos = [...props.todos].filter(todos => todos.status === status);
            console.log('-------- filtered todos ---------', filteredTodos);
            setFiltered(filteredTodos);
        }
    }


    const handleChange = (e) => {
        props.setText(e.target.value);
    };


    let taskElements = filtered.map(e => <StateButtons title={e.title} key={e.id} removeItem={props.removeItem} setTodos={props.setTodos} todos={props.todos} id={e.id} date={e.date} setText={props.setText} text={props.text} />)
    console.log([props.todos])
    return (
        <form onSubmit={handleSubmit}>
            <div className="title">
                <h1>ToDo</h1>
            </div>
            <div className="mainInput">
                <input
                    type="text"
                    placeholder="I want to..."
                    value={props.text}
                    onChange={handleChange}
                    className="input"
                ></input>
            </div>
            <div className="mainInterface">
                <div>
                    <button type="button" className="buttons" onClick={() => todoFilter("All")}
                    >All</button>
                    <button type="button" className="buttons" onClick={() => todoFilter("Done")}
                    >Done</button>
                    <button type="button" className="buttons" onClick={() => todoFilter("undone")}
                    >Undone</button>
                </div>
                <div className="saideInterface">
                    <h4>Sort by date:</h4>
                    <button className="arrows" type="button">⤊</button>
                    <button className="arrows" type="button">⤋</button>
                </div>
            </div>
            <div className="todoList">
                {taskElements}
            </div>
            <div className="mainSelect">
                <button className="select" type="button">❮</button>
                <button className="select" type="button">1</button>
                <button className="select" type="button">2</button>
                <button className="select" type="button">3</button>
                <button className="select" type="button">4</button>
                <button className="select" type="button">5</button>
                <button className="select" type="button">❯</button>
            </div>
        </form>
    );
}

export default TodoList;
