const Pagination = (todosPerPage, totalTodos) => {
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(totalTodos / todosPerPage); i++) {
        pageNumbers.push(i);
    }
    return (
        <nav>
            <ul className="mainSelect">
                {pageNumbers.map((number => (
                    <li key={number} className="mainSelect">
                        <a href="!#" className="mainSelect">
                            {number}
                        </a>
                    </li>
                )))}
            </ul>
        </nav>
    )
}

export default Pagination;
