import "./style.css"
import { useState } from "react";

const StateButton = (props, { editItem }) => {
    const [currentTitle, setCurrentTitle] = useState(props.title);
    const [saveItem, setSaveItem] = useState([]);
    console.log(props.todos);

    const removeItem = (id) => {
        props.setTodos((prevState) =>
            prevState.filter((props) => {
                return props.id !== id;
            })
        );
    };

    const makeComplete = (id) => {
        props.setTodos(
            props.todos.map((todos) => {
                if (props.id === props.index) {
                    return { ...todos, status: "Done" };
                }
                return todos;
            })
        );
    };


    const edit = (cItem, e) => {
        setSaveItem(props);
        if (e.keyCode === 27) {
            editItem([saveItem.id, saveItem.title]);
            setCurrentTitle(props.title);
            e.target.blur()
        }
        if (e.keyCode === 13) {
            editItem([props.id, e.target.value]);
            setCurrentTitle(props.title);
            e.target.blur()
        }
    }

    return (
        <div className="todoElement">
            <div className="inElement">
                <input type="checkbox" className="buttonComplete" key={props.id} onClick={() => {
                    makeComplete(props.id);
                }} />
                <input value={currentTitle} onChange={(e) => {
                    currentTitle = e.target.value
                }} onKeyDown={() => {
                    edit([props.id, currentTitle])
                }} />
            </div>
            <div className="inElement">
                <div>{props.date}</div>
                <input type="button" value="ðŸ—‘" className="buttonDelete" key={props.id} onClick={() => {
                    removeItem(props.id);
                }} />
            </div>
        </div>
    )
}


export default StateButton;
