import { useState } from "react";
import CompleteButton from "./CompleteButton";

const TodoList = () => {
    const [text, setText] = useState("");
    const [todos, setTodos] = useState([]);
    const handleSubmit = (e) => {
        e.preventDefault();
        setTodos((prevValue) => [
            ...prevValue,
            { title: text },
        ]);
        setText("");
    };
    const handleChange = (e) => {
        setText(e.target.value);
    };
    let taskElements = todos.map(e => <CompleteButton title={e.title} />)
    return (
        <form onSubmit={handleSubmit}>
            <div className="title">
                <h1>ToDO</h1>
            </div>
            <div class="mainInput">
                <input
                    type="text"
                    required
                    placeholder="I want to..."
                    value={text}
                    onChange={handleChange}
                    className="input"
                ></input>
            </div>
            <div class="mainInterface">
                <div>
                    <button class="buttons">All</button>
                    <button class="buttons">Done</button>
                    <button class="buttons">Undone</button>
                </div>
                <div class="saideInterface">
                    <h4>Sort by date:</h4>
                    <button class="arrows">⤊</button>
                    <button class="arrows">⤋</button>
                </div>
            </div>
            <div className="todoList">
                {/* {todos.map((todo, index) => {
                    return (
                        <div
                            key={index}
                        >
                            <div className="todoList">
                                <div className="todoElement">{todo.title}</div>
                            </div>
                        </div>
                    )
                })} */}
                {taskElements}
            </div>
            <div class="mainSelect">
                <button class="select">❮</button>
                <button class="select">1</button>
                <button class="select">2</button>
                <button class="select">3</button>
                <button class="select">4</button>
                <button class="select">5</button>
                <button class="select">❯</button>
            </div>

        </form>
    );
}

export default TodoList;
