import "./style.css"
import { useState } from "react";

const StateButton = (props) => {

    const [status, setStatus] = useState(props.todo.status)

    const removeItem = (id) => {
        props.setTodos((prevState) =>
            prevState.filter((props) => {
                return props.id !== id;
            })
        );
    };
    console.log(props.status);

    return (
        <div className="todoElement">
            <div className="inElement">
                <input type="checkbox" className="buttonComplete" checked={status === "Done" ? true : false} key={props.todo.id} onChange={() => {
                    let newStatus
                    if (props.todo.status === "Done") {
                        newStatus = "Undone";
                    } else {
                        newStatus = "Done"
                    }
                    setStatus(newStatus);
                    props.changeStatus(newStatus, props.todo.id)
                }} />
                <div>
                    {props.todo.title}
                </div>
            </div>
            <div className="inElement">
                <div>{props.todo.date}</div>
                <input type="button" value="ðŸ—‘" className="buttonDelete" key={props.todo.id} onClick={() => {
                    removeItem(props.todo.id);
                }} />
            </div>
        </div>
    )
}


export default StateButton;
