import React from 'react';
import axios from 'axios';

const API_GET_TODOS = "http://localhost:3501/api/todos";

const Pagination = async ({ todosPerPage, totalTodos, paginate, filter }) => {
    const href = API_GET_TODOS + `?${filter !== '' && `filterBy=${filter}&`}order=asc`;
    await axios.get(href);
    const pageNumbers = [];
    pageNumbers.push(href);
    for (let i = 1; i <= (Math.ceil(totalTodos / todosPerPage)); i++) {
        pageNumbers.push(i);
    }

    return (
        <div>
            <div className="mainSelect">
                {totalTodos > 0 ?
                    <div className="inSelect">
                        <button onClick={() => { paginate(1) }} className="selectArrow">❮</button>
                        {pageNumbers.map((number => (
                            <div key={number} select>
                                <button onClick={() => paginate(number)} href="!#" className="select">
                                    {number}
                                </button>
                            </div>
                        )))}
                        <button onClick={() => { paginate(Math.ceil(totalTodos / todosPerPage)) }} className="selectArrow">❯</button>
                    </div>
                    : ""}
            </div>
        </div>
    )
}

export default Pagination;
