import "../css/style.css";
import { useState } from "react";


const TodoItem = ({ todo, removeItem, editItem }) => {
    const [currentTitle, setCurrentTitle] = useState(todo.name);
    const [showInput, setShowInput] = useState(false);

    return (
        <div key={todo.uuid}>
            <div className="todoElement">
                <div className="inElement">
                    <input
                        type="checkbox"
                        className="buttonComplete"
                        onClick={(e) => {
                            editItem(e, currentTitle, { id: todo.id, name: todo.name, done: todo.done === 'done' ? 'undone' : 'done', setShowInput, setCurrentTitle });
                        }}
                    />
                    {!showInput ? (
                        <span onDoubleClick={() => setShowInput(true)}>
                            {currentTitle}
                        </span>
                    ) : (
                        <input
                            value={currentTitle}
                            onKeyDown={(e) =>
                                editItem(e, currentTitle, todo, setShowInput, setCurrentTitle)
                            }
                            className="sideInput"
                            onChange={(e) => setCurrentTitle(e.target.value)}
                        />
                    )}
                </div>
                <div className="inElement">
                    <div>
                        {todo.createdAt.slice(0, 10) + ' ' + todo.createdAt.slice(11, -5)}
                    </div>
                    <input
                        type="button"
                        value="ðŸ—‘"
                        className="buttonDelete"
                        onClick={() => {
                            removeItem(todo.id);
                        }}
                    />
                </div>
            </div>
        </div>
    );
};

export default TodoItem;
