import { useState } from "react";
import CompleteButton from "./CompleteButton";

const TodoList = () => {
    const [text, setText] = useState("");
    const [todos, setTodos] = useState([]);
    const handleSubmit = (e) => {
        e.preventDefault();
        setTodos((prevValue) => [
            ...prevValue,
            { title: text },
        ]);
        setText("");
    };
    const handleChange = (e) => {
        setText(e.target.value);
    };
    const ToggleClass = (id) => {
        setTodos(
            todos.map((todo, index) => {
                if (id === index) {
                    return { ...todo, status: "Complete" };
                }
                return todo;
            })
        );
    };
    let taskElements = todos.map(e => <CompleteButton title={e.title} />)
    return (
        <form onSubmit={handleSubmit}>
            <div className="title">
                <h1>ToDo</h1>
            </div>
            <div className="mainInput">
                <input
                    type="text"
                    required
                    placeholder="I want to..."
                    value={text}
                    onChange={handleChange}
                    className="input"
                ></input>
            </div>
            <div className="mainInterface">
                <div>
                    <button className="buttons">All</button>
                    <button className="buttons">Done</button>
                    <button className="buttons">Undone</button>
                </div>
                <div className="saideInterface">
                    <h4>Sort by date:</h4>
                    <button className="arrows">⤊</button>
                    <button className="arrows">⤋</button>
                </div>
            </div>
            <div className="todoList">
                {taskElements}
            </div>
            <div className="mainSelect">
                <button className="select">❮</button>
                <button className="select">1</button>
                <button className="select">2</button>
                <button className="select">3</button>
                <button className="select">4</button>
                <button className="select">5</button>
                <button className="select">❯</button>
            </div>

        </form>
    );
}

export default TodoList;
