import './App.css';
import TodoList from './TodoList';
import { useState } from 'react';


const App = () => {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");
  const removeItem = (id) => {
    setTodos((...prevState) =>
      prevState.filter((index) => {
        return index !== id;
      })
    );
  };
  return (
    <div>
      <TodoList todos={todos} setTodos={setTodos} text={text} setText={setText} />
    </div>
  );
}

export default App;
