import React from 'react';

const Paginate = ({ todosPerPage, numbersOfTodos, currentPage, setCurrentPage }) => {
    const pageNumbers = [];
    for (let i = 1; i <= (Math.ceil(numbersOfTodos / todosPerPage)); i++) {
        pageNumbers.push(i);
    }
    console.log(pageNumbers);
    return (
        <div>
            <div className="mainSelect">
                {numbersOfTodos > 0 ?
                    <div className="inSelect">
                        <button onClick={() => { setCurrentPage(1) }} className="selectArrow">❮</button>
                        {pageNumbers.map((number => (
                            <div key={number} select>
                                <button onClick={() => setCurrentPage(number)} href="!#" className="select">
                                    {number}
                                </button>
                            </div>
                        )))}
                        <button onClick={() => { setCurrentPage(Math.ceil(numbersOfTodos / todosPerPage)) }} className="selectArrow">❯</button>
                    </div>
                    : ""}
            </div>
        </div>
    )
}

export default Paginate;



