import "../css/style.css"
import { useState, useEffect } from "react";

const StateButton = (props, { todo }) => {
    console.log(props)
    const [currentTitle, setCurrentTitle] = useState('');
    const [status, setStatus] = useState('')
    useEffect(() => {
        setCurrentTitle(props.todo.title)
        setStatus(props.todo.status)
    }, [])
    const removeItem = (id) => {
        props.setTodos((prevState) =>
            prevState.filter((props) => {
                return props.id !== id;
            })
        );
    };
    const editText = (e) => {
        props.editItem(currentTitle, props.todo.id, e);
    }

    return (
        <div key={props.todo.id}>
            <div className="todoElement">
                <div className="inElement">
                    <input type="checkbox" className="buttonComplete" checked={status === "Done" ? true : false} onChange={() => {
                        let newStatus
                        if (props.todo.status === "Done") {
                            newStatus = "Undone";
                        } else {
                            newStatus = "Done"
                        }
                        setStatus(newStatus);
                        props.changeStatus(newStatus, props.todo.id)
                    }} />
                    <div>
                        <input value={currentTitle} onKeyDown={(e) => editText(e)} onChange={(e) => setCurrentTitle(e.target.value)}
                        />
                    </div>
                </div>
                <div className="inElement">
                    <div>{props.todo.date}</div>
                    <input type="button" value="ðŸ—‘" className="buttonDelete" onClick={() => {
                        removeItem(props.todo.id);
                    }} />
                </div>
            </div>
        </div>
    )
}


export default StateButton;
