import "../css/style.css"
import { useState, useEffect } from "react";

const StateButton = (props) => {
    const name = props.todo.title
    const [currentTitle, setCurrentTitle] = useState(name);
    const [status, setStatus] = useState('')
    console.log(props.todo);

    // useEffect(() => {
    //     // setCurrentTitle(props.todo.title)
    //     // setStatus(props.todo.status)
    // }, [])

    const editItem = (e, currentTitle, todo) => {
        console.log(e);
        const item = props.filtered.find(el => el.id === todo.id);
        const oldName = item.title
        item.title = currentTitle
        const itemIndex = props.filtered.findIndex(el => el.id === todo.id);
        if (e.keyCode === 13) {
            const arr = props.filtered
            arr[itemIndex] = item
            props.setTodos(arr);
        }
        if (e.keyCode === 27) {
            e.target.blur();
            e.target.value = oldName;

        }

    }

    const removeItem = (id) => {
        props.setTodos((prevState) =>
            prevState.filter((props) => {
                return props.id !== id;
            })
        );
    };

    return (
        <div key={props.todo.id}>
            <div className="todoElement">
                <div className="inElement">
                    <input type="checkbox" className="buttonComplete" checked={status === "Done" ? true : false} onChange={() => {
                        let newStatus
                        if (props.todo.status === "Done") {
                            newStatus = "Undone";
                        } else {
                            newStatus = "Done"
                        }
                        setStatus(newStatus);
                        props.changeStatus(newStatus, props.todo.id)
                    }} />
                    <div>
                        <input value={currentTitle} onKeyDown={(e) => editItem(e, currentTitle, props.todo)} onChange={(e) => setCurrentTitle(e.target.value)}
                        />
                    </div>
                </div>
                <div className="inElement">
                    <div>{props.todo.date}</div>
                    <input type="button" value="ðŸ—‘" className="buttonDelete" onClick={() => {
                        removeItem(props.todo.id);
                    }} />
                </div>
            </div>
        </div>
    )
}


export default StateButton;
