import "../css/style.css";

const todoItem = ({ editItem, todo, removeItem, changeStatus, currentTitle, setCurrentTitle, showInput, setShowInput }) => {
    return (
        <div key={todo.uuid}>
            <div className="todoElement">
                <div className="inElement">
                    <input
                        type="checkbox"
                        className="buttonComplete"
                        defaultChecked={todo.done === true ? true : false}
                        onClick={() => {
                            changeStatus(todo);
                        }}
                    />
                    {!showInput ? (
                        <span onDoubleClick={() => setShowInput(true)}>
                            {currentTitle}
                        </span>
                    ) : (
                        <input
                            value={currentTitle}
                            onKeyDown={(e) =>
                                editItem(e, currentTitle, todo)
                            }
                            className="sideInput"
                            onChange={(e) => setCurrentTitle(e.target.value)}
                        />
                    )}
                </div>
                <div className="inElement">
                    <div>
                        {new Date(Date.parse(todo.updatedAtt)).toLocaleString()}
                    </div>
                    <input
                        type="button"
                        value="ðŸ—‘"
                        className="buttonDelete"
                        onClick={() => {
                            removeItem(todo.uuid);
                        }}
                    />
                </div>
            </div>
        </div>
    );
};

export default todoItem;
