import "../css/style.css"
import { useState } from "react";

// const StateButton = (props) => {
const StateButton = ({ todo, setTodos, filtered, changeStatus }) => {
    const [currentTitle, setCurrentTitle] = useState(todo.title);
    const [status, setStatus] = useState('')
    console.log(todo);

    // useEffect(() => {
    //     // setCurrentTitle(todo.title)
    //     // setStatus(todo.status)
    // }, [])

    const editItem = (e, currentTitle, todo) => {
        console.log(e);
        const item = filtered.find(el => el.id === todo.id);
        const oldName = item.title
        item.title = currentTitle
        const itemIndex = filtered.findIndex(el => el.id === todo.id);
        if (e.keyCode === 13) {
            console.log('hello');
            const arr = filtered
            arr[itemIndex] = item
            setTodos(arr);
        }
        if (e.keyCode === 27) {
            e.target.blur();
            setCurrentTitle(todo.title)
        }

    }

    const removeItem = (id) => {
        setTodos((prevState) =>
            prevState.filter((props) => {
                return id !== id;
            })
        );
    };

    return (
        <div key={todo.id}>
            <div className="todoElement">
                <div className="inElement">
                    <input type="checkbox" className="buttonComplete" checked={status === "Done" ? true : false} onChange={() => {
                        let newStatus
                        if (todo.status === "Done") {
                            newStatus = "Undone";
                        } else {
                            newStatus = "Done"
                        }
                        setStatus(newStatus);
                        changeStatus(newStatus, todo.id)
                    }} />
                    <div>
                        <input value={currentTitle} onKeyDown={(e) => editItem(e, currentTitle, todo)} onChange={(e) => setCurrentTitle(e.target.value)}
                        />
                    </div>
                </div>
                <div className="inElement">
                    <div>{todo.date}</div>
                    <input type="button" value="ðŸ—‘" className="buttonDelete" onClick={() => {
                        removeItem(todo.id);
                    }} />
                </div>
            </div>
        </div>
    )
}


export default StateButton;
