import './App.css';
import TodoList from './TodoList';
import { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';


const App = () => {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");
  const [filter, setFilter] = useState("all");
  const [filtered, setFiltered] = useState(todos);

  useEffect(() => {
    console.log(2222)
    setFiltered(todos)
  }, [todos, filtered])

  const handleChangeTodos = (e) => {
    e.preventDefault();
    setTodos((prevValue) => [
      ...prevValue,
      { id: uuidv4(), title: text, date: date(), status: "undone" },
    ]);
    setText("");
  }

  const todoFilter = (status) => {
    if (status === "All") {
      setFiltered(todos);
    } else {
      let filteredTodos = [...todos].filter(todos => todos.status === status);
      setFiltered(filteredTodos);
    }
  }

  const date = () => {
    const date = new Date();
    return date.getDate() + '.' + date.getMonth() + '.' + date.getFullYear() + ' ' + date.getHours() + ':' + date.getMinutes() + ':' + date.getSeconds()
  }


  return (
    <div>
      <TodoList todos={todos} setTodos={setTodos} text={text} setText={setText} handleChangeTodos={handleChangeTodos} filtered={filtered} todoFilter={todoFilter} />
    </div>
  );
}

export default App;
