import React, { useContext, useState } from "react";
import { AuthContext } from "../context/auth.context";
import { useHttp } from "../hooks/http.hook";
import { useNavigate } from "react-router-dom";


export const AuthPage = () => {
    const auth = useContext(AuthContext)
    const { loading, request } = useHttp();
    const navigate = useNavigate();
    const [form, setForm] = useState({
        email: "", password: "",
    })

    const changeHandler = event => {
        setForm({ ...form, [event.target.name]: event.target.value })
    }

    const registerHandler = async () => {
        try {
            const data = await request("https://back-end-api-1.herokuapp.com/api/register", "POST", form);
            navigate("/auth", { replace: true });
            alert("user was created!")
        } catch (e) {
            console.log(e);
            alert(e);
        }
    }

    const loginHandler = async () => {
        try {
            const data = await request('https://back-end-api-1.herokuapp.com/api/login', 'POST', { ...form })
            auth.login(data.data.token, data.data.userId);
            navigate("/app", { replace: true });
        } catch (e) {
            console.log(e);
            alert(e);
        }
    }


    return (
        <div className="login">
            <div className="title">
                <h1>Welcome!</h1>
            </div>
            <form method="post">
                <input
                    type="text"
                    name="email"
                    placeholder="Email..."
                    required="required"
                    className="inputAuth"
                    id="email"
                    onChange={changeHandler} />
                <input
                    type="password"
                    name="password"
                    placeholder="Password..."
                    required="required"
                    className="inputAuth"
                    id="password"
                    onChange={changeHandler} />
                <div className="buttonsAuthContainer">
                    <button
                        type="submit"
                        className="buttonsAuth"
                        disabled={loading}
                        onClick={loginHandler}
                    >
                        Sign in</button>
                    <button
                        type="submit"
                        className="buttonsAuth"
                        disabled={loading}
                        onClick={registerHandler}
                    >Sign up</button>
                </div>
            </form>
        </div>
    )
}

export default AuthPage;
