import StateButtons from "./StateButtons";
import { v4 as uuidv4 } from 'uuid';

const TodoList = (props) => {
    const handleSubmit = (e) => {
        e.preventDefault();
        props.setTodos((prevValue) => [
            ...prevValue,
            { id: uuidv4(), title: props.text },
        ]);
        props.setText("");
        console.log(props.todos);
    };

    const removeItem = (id) => {
        props.setTodos((prevState) =>
            prevState.filter((a, index) => {
                return index !== id;
            })
        );
    };
    console.log(props)

    const handleChange = (e) => {
        props.setText(e.target.value);
    };

    let taskElements = props.todos.map(e => <StateButtons title={e.title} removeItem={props.removeItem} setTodos={props.setTodos} />)

    return (
        <form onSubmit={handleSubmit}>
            <div className="title">
                <h1>ToDo</h1>
            </div>
            <div className="mainInput">
                <input
                    type="text"
                    required
                    placeholder="I want to..."
                    value={props.text}
                    onChange={handleChange}
                    className="input"
                ></input>
            </div>
            <div className="mainInterface">
                <div>
                    <button className="buttons">All</button>
                    <button className="buttons">Done</button>
                    <button className="buttons">Undone</button>
                </div>
                <div className="saideInterface">
                    <h4>Sort by date:</h4>
                    <button className="arrows">⤊</button>
                    <button className="arrows">⤋</button>
                </div>
            </div>
            <div className="todoList">
                {props.todos
                    .map(
                        (todo, index) =>
                            <div
                                key={index}
                            >
                                <div className="todo_text">
                                    {todo.title}
                                </div>
                                <div className="inElement" >
                                    <div>23/11/2021</div>
                                    <input type="button" value="🗑" className="buttonDelete" key={props.index} onClick={() => {
                                        removeItem(props.index);
                                    }} />
                                </div>
                            </div>
                    )}
            </div>
            <div className="mainSelect">
                <button className="select">❮</button>
                <button className="select">1</button>
                <button className="select">2</button>
                <button className="select">3</button>
                <button className="select">4</button>
                <button className="select">5</button>
                <button className="select">❯</button>
            </div>

        </form>
    );
}

export default TodoList;
