import React, { useState } from 'react';
import axios from "axios";
const API_GET_TODOS = "http://localhost:3501/api/todos";
// import Pagination from '@mui/material/Pagination';
// import Stack from '@mui/material/Stack';

const Paginate = (filter, totalTodos, todosPerPage, paginate) => {
    const [result, setResault] = useState([]);
    const pagination = async () => {
        const href = API_GET_TODOS + `?${filter !== '' && `filterBy=${filter}&`}order=desc`;
        setResault(await axios.get(href))
    }
    console.log(result);
    return (
        <div>
            <div className="mainSelect">
                {totalTodos > 0 ?
                    <div className="inSelect">
                        <button onClick={() => { paginate(1) }} className="selectArrow">❮</button>
                        {result.map((number => (
                            <div key={number} select>
                                <button onClick={() => paginate(number)} href="!#" className="select">
                                    {number}
                                </button>
                            </div>
                        )))}
                        <button onClick={() => { paginate(Math.ceil(totalTodos / todosPerPage)) }} className="selectArrow">❯</button>
                    </div>
                    : ""}
            </div>
        </div>
    )
}

export default Paginate;


