import "./style.css"

const StateButton = (props) => {

    const removeItem = (id) => {
        props.setTodos((prevState) =>
            prevState.filter((props) => {
                return props.id !== id;
            })
        );
    };


    return (
        <div className="todoElement">
            <div className="inElement">
                <input type="checkbox" className="buttonComplete" checked={props.status === "Done" ? true : false} key={props.id} onChange={() => {
                    let newStatus
                    if (props.status === "Done") {
                        newStatus = "Undone";
                    } else {
                        newStatus = "Done"
                    }
                    props.changeStatus(newStatus, props.id)
                }} />
                <div>
                    {props.title}
                </div>
            </div>
