import "./style.css"

const StateButton = (props) => {
    const removeItem = (id) => {
        props.setTodos((prevState) =>
            prevState.filter((props) => {
                return props.id !== id;
            })
        );
    };
    const ChangeText = (title) => {
        props.setText((prevState) => {
            prevState.map((props) => {
                return props.title == e.target.value;
            })
        })
    }

    return (
        <div className="todoElement">
            <div className="inElement">
                <input type="checkbox" className="buttonComplete" />
                <div onClick={(props) => {
                    ChangeText(props.setText);
                }
                }>{props.title}</div>
            </div>
